mapper.select:
select x.*,y.types china,q.sren green from feiyong x left join feiyongleixing y on x.type=y.tid left join t_person q on x.ren=q.sid
x.price like concat('%','${seo.price}','%')
limit ${key_off},${pageSize}

select x.*,q.sren yewu,s.rname yewu2 from Ordering x left join t_person q on x.ding=q.sid left join Orderingx s on x.tao=s.rid
x.price like concat('%','${ordering.price}','%')
limit ${key_off},${pageSize}
